TranslateAgentAsyncWaiter::~TranslateAgentAsyncWaiter() = default;

void TranslateAgentAsyncWaiter::TranslateFrame(const std::string& translate_script, const std::string& source_lang, const std::string& target_lang,
    bool* out_cancelled, std::string* out_original_lang, std::string* out_translated_lang, ::translate::TranslateErrors* out_error)
{
    base::RunLoop loop;
    proxy_->TranslateFrame(std::move(translate_script), std::move(source_lang), std::move(target_lang),
        base::BindOnce(
            [](base::RunLoop* loop, bool* out_cancelled, std::string* out_original_lang, std::string* out_translated_lang,
                ::translate::TranslateErrors* out_error, bool cancelled, const std::string& original_lang, const std::string& translated_lang,
                ::translate::TranslateErrors error) {
                *out_cancelled = std::move(cancelled);
                *out_original_lang = std::move(original_lang);
                *out_translated_lang = std::move(translated_lang);
                *out_error = std::move(error);
                loop->Quit();
            },
            &loop, out_cancelled, out_original_lang, out_translated_lang, out_error));
    loop.Run();
}

