bool CdmFile_Write_ForwardToCallback::Accept(mojo::Message* message)
{
    DCHECK(message->is_serialized());
    internal::CdmFile_Write_ResponseParams_Data* params = reinterpret_cast<internal::CdmFile_Write_ResponseParams_Data*>(message->mutable_payload());

    // Validation for CdmFile.1
    bool success = true;
    CdmFile::Status p_status {};
    CdmFile_Write_ResponseParamsDataView input_data_view(params, message);

    if (success && !input_data_view.ReadStatus(&p_status))
        success = false;
    if (!success) {
        ReportValidationErrorForMessage(message, mojo::internal::VALIDATION_ERROR_DESERIALIZATION_FAILED, CdmFile::Name_, 1, true);
        return false;
    }
    if (!callback_.is_null())
        std::move(callback_).Run(std::move(p_status));
    return true;
}

