// static
bool CdmFileStubDispatch::AcceptWithResponder(CdmFile* impl, mojo::Message* message, std::unique_ptr<mojo::MessageReceiverWithStatus> responder)
{
    [[maybe_unused]] const bool message_is_sync = message->has_flag(mojo::Message::kFlagIsSync);
    [[maybe_unused]] const uint64_t request_id = message->request_id();
    switch (static_cast<messages::CdmFile>(message->header()->name)) {
    case messages::CdmFile::kRead: {
        internal::CdmFile_Read_Params_Data* params = reinterpret_cast<internal::CdmFile_Read_Params_Data*>(message->mutable_payload());

        // Validation for CdmFile.0
        bool success = true;
        CdmFile_Read_ParamsDataView input_data_view(params, message);

        if (!success) {
            ReportValidationErrorForMessage(message, mojo::internal::VALIDATION_ERROR_DESERIALIZATION_FAILED, CdmFile::Name_, 0, false);
            return false;
        }
        CdmFile::ReadCallback callback = CdmFile_Read_ProxyToResponder::CreateCallback(*message, std::move(responder));
        // A null |impl| means no implementation was bound.
        DCHECK(impl);
        impl->Read(std::move(callback));
        return true;
    }
    case messages::CdmFile::kWrite: {
        internal::CdmFile_Write_Params_Data* params = reinterpret_cast<internal::CdmFile_Write_Params_Data*>(message->mutable_payload());

        // Validation for CdmFile.1
        bool success = true;
        WTF::Vector<uint8_t> p_data {};
        CdmFile_Write_ParamsDataView input_data_view(params, message);

        if (success && !input_data_view.ReadData(&p_data))
            success = false;
        if (!success) {
            ReportValidationErrorForMessage(message, mojo::internal::VALIDATION_ERROR_DESERIALIZATION_FAILED, CdmFile::Name_, 1, false);
            return false;
        }
        CdmFile::WriteCallback callback = CdmFile_Write_ProxyToResponder::CreateCallback(*message, std::move(responder));
        // A null |impl| means no implementation was bound.
        DCHECK(impl);
        impl->Write(std::move(p_data), std::move(callback));
        return true;
    }
    }
    return false;
}
