CdmFileAsyncWaiter::CdmFileAsyncWaiter(CdmFile* proxy)
    : proxy_(proxy)
{
}

CdmFileAsyncWaiter::~CdmFileAsyncWaiter() = default;

void CdmFileAsyncWaiter::Read(CdmFile::Status* out_status, WTF::Vector<uint8_t>* out_data)
{
    base::RunLoop loop;
    proxy_->Read(base::BindOnce(
        [](base::RunLoop* loop, CdmFile::Status* out_status, WTF::Vector<uint8_t>* out_data, CdmFile::Status status, const WTF::Vector<uint8_t>& data) {
            *out_status = std::move(status);
            *out_data = std::move(data);
            loop->Quit();
        },
        &loop, out_status, out_data));
    loop.Run();
}

void CdmFileAsyncWaiter::Write(const WTF::Vector<uint8_t>& data, CdmFile::Status* out_status)
{
    base::RunLoop loop;
    proxy_->Write(std::move(data),
        base::BindOnce(
            [](base::RunLoop* loop, CdmFile::Status* out_status, CdmFile::Status status) {
                *out_status = std::move(status);
                loop->Quit();
            },
            &loop, out_status));
    loop.Run();
}

CdmFile::Status CdmFileAsyncWaiter::Write(const WTF::Vector<uint8_t>& data)
{
    CdmFile::Status async_wait_result;
    Write(std::move(data), &async_wait_result);
    return async_wait_result;
}

