ImageDecoderAsyncWaiter::ImageDecoderAsyncWaiter(ImageDecoder* proxy)
    : proxy_(proxy)
{
}

ImageDecoderAsyncWaiter::~ImageDecoderAsyncWaiter() = default;

void ImageDecoderAsyncWaiter::DecodeImage(::mojo_base::BigBuffer encoded_data, ImageCodec codec, bool shrink_to_fit, int64_t max_size_in_bytes,
    const ::gfx::Size& desired_image_frame_size, ::base::TimeDelta* out_decoding_duration, ::SkBitmap* out_decoded_image)
{
    base::RunLoop loop;
    proxy_->DecodeImage(std::move(encoded_data), std::move(codec), std::move(shrink_to_fit), std::move(max_size_in_bytes), std::move(desired_image_frame_size),
        base::BindOnce(
            [](base::RunLoop* loop, ::base::TimeDelta* out_decoding_duration, ::SkBitmap* out_decoded_image, ::base::TimeDelta decoding_duration,
                const ::SkBitmap& decoded_image) {
                *out_decoding_duration = std::move(decoding_duration);
                *out_decoded_image = std::move(decoded_image);
                loop->Quit();
            },
            &loop, out_decoding_duration, out_decoded_image));
    loop.Run();
}

void ImageDecoderAsyncWaiter::DecodeAnimation(
    ::mojo_base::BigBuffer encoded_data, bool shrink_to_fit, int64_t max_size_in_bytes, std::vector<AnimationFramePtr>* out_decoded_image)
{
    base::RunLoop loop;
    proxy_->DecodeAnimation(std::move(encoded_data), std::move(shrink_to_fit), std::move(max_size_in_bytes),
        base::BindOnce(
            [](base::RunLoop* loop, std::vector<AnimationFramePtr>* out_decoded_image, std::vector<AnimationFramePtr> decoded_image) {
                *out_decoded_image = std::move(decoded_image);
                loop->Quit();
            },
            &loop, out_decoded_image));
    loop.Run();
}

std::vector<AnimationFramePtr> ImageDecoderAsyncWaiter::DecodeAnimation(::mojo_base::BigBuffer encoded_data, bool shrink_to_fit, int64_t max_size_in_bytes)
{
    std::vector<AnimationFramePtr> async_wait_result;
    DecodeAnimation(std::move(encoded_data), std::move(shrink_to_fit), std::move(max_size_in_bytes), &async_wait_result);
    return async_wait_result;
}

