    uint8_t p_report_id {};
    std::optional<std::vector<uint8_t>> p_buffer {};
    HidConnection_Read_ResponseParamsDataView input_data_view(params, message);

    if (success)
        p_success = input_data_view.success();
    if (success)
        p_report_id = input_data_view.report_id();
    if (success && !input_data_view.ReadBuffer(&p_buffer))
        success = false;
    if (!success) {
        ReportValidationErrorForMessage(message, mojo::internal::VALIDATION_ERROR_DESERIALIZATION_FAILED, HidConnection::Name_, 0, true);
        return false;
    }
    if (!callback_.is_null())
        std::move(callback_).Run(std::move(p_success), std::move(p_report_id), std::move(p_buffer));
