ServiceAsyncWaiter::ServiceAsyncWaiter(Service* proxy)
    : proxy_(proxy)
{
}

ServiceAsyncWaiter::~ServiceAsyncWaiter() = default;

void ServiceAsyncWaiter::OnStart(const ::service_manager::Identity& identity,
    ::mojo::PendingReceiver<::service_manager::mojom::Connector>* out_connector_receiver,
    ::mojo::PendingAssociatedReceiver<::service_manager::mojom::ServiceControl>* out_control_receiver)
{
    base::RunLoop loop;
    proxy_->OnStart(std::move(identity),
        base::BindOnce(
            [](base::RunLoop* loop, ::mojo::PendingReceiver<::service_manager::mojom::Connector>* out_connector_receiver,
                ::mojo::PendingAssociatedReceiver<::service_manager::mojom::ServiceControl>* out_control_receiver,
                ::mojo::PendingReceiver<::service_manager::mojom::Connector> connector_receiver,
                ::mojo::PendingAssociatedReceiver<::service_manager::mojom::ServiceControl> control_receiver) {
                *out_connector_receiver = std::move(connector_receiver);
                *out_control_receiver = std::move(control_receiver);
                loop->Quit();
            },
            &loop, out_connector_receiver, out_control_receiver));
    loop.Run();
}

void ServiceAsyncWaiter::OnBindInterface(
    const ::service_manager::BindSourceInfo& source, const std::string& interface_name, ::mojo::ScopedMessagePipeHandle interface_pipe)
{
    base::RunLoop loop;
    proxy_->OnBindInterface(
        std::move(source), std::move(interface_name), std::move(interface_pipe), base::BindOnce([](base::RunLoop* loop) { loop->Quit(); }, &loop));
    loop.Run();
}

