void DeviceAsyncWaiter::GetPhotoState(::media::mojom::PhotoStatePtr* out_capabilities)
{
    base::RunLoop loop;
    proxy_->GetPhotoState(base::BindOnce(
        [](base::RunLoop* loop, ::media::mojom::PhotoStatePtr* out_capabilities, ::media::mojom::PhotoStatePtr capabilities) {
            *out_capabilities = std::move(capabilities);
            loop->Quit();
        },
        &loop, out_capabilities));
    loop.Run();
}

::media::mojom::PhotoStatePtr DeviceAsyncWaiter::GetPhotoState()
{
    ::media::mojom::PhotoStatePtr async_wait_result;
    GetPhotoState(&async_wait_result);
    return async_wait_result;
}

void DeviceAsyncWaiter::SetPhotoOptions(::media::mojom::PhotoSettingsPtr settings, bool* out_success)
{
    base::RunLoop loop;
    proxy_->SetPhotoOptions(std::move(settings),
        base::BindOnce(
            [](base::RunLoop* loop, bool* out_success, bool success) {
                *out_success = std::move(success);
                loop->Quit();
            },
            &loop, out_success));
    loop.Run();
}

bool DeviceAsyncWaiter::SetPhotoOptions(::media::mojom::PhotoSettingsPtr settings)
{
    bool async_wait_result;
    SetPhotoOptions(std::move(settings), &async_wait_result);
    return async_wait_result;
}

