// static
bool StructTraits<::blink::mojom::blink::DataElementBytes::DataView, ::blink::mojom::blink::DataElementBytesPtr>::Read(
    ::blink::mojom::blink::DataElementBytes::DataView input, ::blink::mojom::blink::DataElementBytesPtr* output)
{
    bool success = true;
    ::blink::mojom::blink::DataElementBytesPtr result(::blink::mojom::blink::DataElementBytes::New());

    if (success)
        result->length = input.length();
    if (success && !input.ReadEmbeddedData(&result->embedded_data))
        success = false;
    if (success) {
        result->data = input.TakeData<decltype(result->data)>();
    }
    *output = std::move(result);
    return success;
}

// static
bool StructTraits<::blink::mojom::blink::DataElementFile::DataView, ::blink::mojom::blink::DataElementFilePtr>::Read(
    ::blink::mojom::blink::DataElementFile::DataView input, ::blink::mojom::blink::DataElementFilePtr* output)
{
    bool success = true;
    ::blink::mojom::blink::DataElementFilePtr result(::blink::mojom::blink::DataElementFile::New());

    if (success && !input.ReadPath(&result->path))
        success = false;
    if (success)
        result->offset = input.offset();
    if (success)
        result->length = input.length();
    if (success && !input.ReadExpectedModificationTime(&result->expected_modification_time))
        success = false;
    *output = std::move(result);
    return success;
}

