bool StructTraits<::blink::mojom::blink::DataElementBlob::DataView, ::blink::mojom::blink::DataElementBlobPtr>::Read(
    ::blink::mojom::blink::DataElementBlob::DataView input, ::blink::mojom::blink::DataElementBlobPtr* output)
{
    bool success = true;
    ::blink::mojom::blink::DataElementBlobPtr result(::blink::mojom::blink::DataElementBlob::New());

    if (success) {
        result->blob = input.TakeBlob<decltype(result->blob)>();
    }
    if (success)
        result->offset = input.offset();
    if (success)
        result->length = input.length();
    *output = std::move(result);
    return success;
}

// static
bool UnionTraits<::blink::mojom::blink::DataElement::DataView, ::blink::mojom::blink::DataElementPtr>::Read(
    ::blink::mojom::blink::DataElement::DataView input, ::blink::mojom::blink::DataElementPtr* output)
{
    using UnionType = ::blink::mojom::blink::DataElement;
    using Tag = UnionType::Tag;

    switch (input.tag()) {
    case Tag::kBytes: {
        ::blink::mojom::blink::DataElementBytesPtr result_bytes;
        if (!input.ReadBytes(&result_bytes))
            return false;

        *output = UnionType::NewBytes(std::move(result_bytes));
        break;
    }
    case Tag::kFile: {
        ::blink::mojom::blink::DataElementFilePtr result_file;
        if (!input.ReadFile(&result_file))
            return false;

        *output = UnionType::NewFile(std::move(result_file));
        break;
    }
    case Tag::kBlob: {
        ::blink::mojom::blink::DataElementBlobPtr result_blob;
        if (!input.ReadBlob(&result_blob))
            return false;

        *output = UnionType::NewBlob(std::move(result_blob));
        break;
    }
    default:

        return false;
    }
    return true;
}

