template <typename UnionPtrType> DragItemPtr DragItem::Clone() const
{
    switch (tag_) {
    case Tag::kString:
        return NewString(mojo::Clone(*data_.string));
    case Tag::kFile:
        return NewFile(mojo::Clone(*data_.file));
    case Tag::kBinary:
        return NewBinary(mojo::Clone(*data_.binary));
    case Tag::kFileSystemFile:
        return NewFileSystemFile(mojo::Clone(*data_.file_system_file));
    }
    return nullptr;
}

template <typename T, typename std::enable_if<std::is_same<T, DragItem>::value>::type*> bool DragItem::Equals(const T& other) const
{
    if (tag_ != other.which())
        return false;

    switch (tag_) {
    case Tag::kString:
        return mojo::Equals(*(data_.string), *(other.data_.string));
    case Tag::kFile:
        return mojo::Equals(*(data_.file), *(other.data_.file));
    case Tag::kBinary:
        return mojo::Equals(*(data_.binary), *(other.data_.binary));
    case Tag::kFileSystemFile:
        return mojo::Equals(*(data_.file_system_file), *(other.data_.file_system_file));
    }

    return false;
}
