HidServiceAsyncWaiter::HidServiceAsyncWaiter(HidService* proxy)
    : proxy_(proxy)
{
}

HidServiceAsyncWaiter::~HidServiceAsyncWaiter() = default;

void HidServiceAsyncWaiter::GetDevices(WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr>* out_devices)
{
    base::RunLoop loop;
    proxy_->GetDevices(base::BindOnce(
        [](base::RunLoop* loop, WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr>* out_devices,
            WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> devices) {
            *out_devices = std::move(devices);
            loop->Quit();
        },
        &loop, out_devices));
    loop.Run();
}

WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> HidServiceAsyncWaiter::GetDevices()
{
    WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> async_wait_result;
    GetDevices(&async_wait_result);
    return async_wait_result;
}

void HidServiceAsyncWaiter::RequestDevice(WTF::Vector<HidDeviceFilterPtr> filters, WTF::Vector<HidDeviceFilterPtr> exclusion_filters,
    WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr>* out_devices)
{
    base::RunLoop loop;
    proxy_->RequestDevice(std::move(filters), std::move(exclusion_filters),
        base::BindOnce(
            [](base::RunLoop* loop, WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr>* out_devices,
                WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> devices) {
                *out_devices = std::move(devices);
                loop->Quit();
            },
            &loop, out_devices));
    loop.Run();
}

WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> HidServiceAsyncWaiter::RequestDevice(
    WTF::Vector<HidDeviceFilterPtr> filters, WTF::Vector<HidDeviceFilterPtr> exclusion_filters)
{
    WTF::Vector<::device::mojom::blink::HidDeviceInfoPtr> async_wait_result;
    RequestDevice(std::move(filters), std::move(exclusion_filters), &async_wait_result);
    return async_wait_result;
}

void HidServiceAsyncWaiter::Connect(const WTF::String& device_guid, ::mojo::PendingRemote<::device::mojom::blink::HidConnectionClient> client,
    ::mojo::PendingRemote<::device::mojom::blink::HidConnection>* out_connection)
{
    base::RunLoop loop;
    proxy_->Connect(std::move(device_guid), std::move(client),
        base::BindOnce(
            [](base::RunLoop* loop, ::mojo::PendingRemote<::device::mojom::blink::HidConnection>* out_connection,
                ::mojo::PendingRemote<::device::mojom::blink::HidConnection> connection) {
                *out_connection = std::move(connection);
                loop->Quit();
            },
            &loop, out_connection));
    loop.Run();
}

::mojo::PendingRemote<::device::mojom::blink::HidConnection> HidServiceAsyncWaiter::Connect(
    const WTF::String& device_guid, ::mojo::PendingRemote<::device::mojom::blink::HidConnectionClient> client)
{
    ::mojo::PendingRemote<::device::mojom::blink::HidConnection> async_wait_result;
    Connect(std::move(device_guid), std::move(client), &async_wait_result);
    return async_wait_result;
}

void HidServiceAsyncWaiter::Forget(::device::mojom::blink::HidDeviceInfoPtr device_info)
{
    base::RunLoop loop;
    proxy_->Forget(std::move(device_info), base::BindOnce([](base::RunLoop* loop) { loop->Quit(); }, &loop));
    loop.Run();
}

