ImageDownloaderAsyncWaiter::~ImageDownloaderAsyncWaiter() = default;

void ImageDownloaderAsyncWaiter::DownloadImage(const ::blink::KURL& url, bool is_favicon, const ::gfx::Size& preferred_size, uint32_t max_bitmap_size,
    bool bypass_cache, int32_t* out_http_status_code, WTF::Vector<::SkBitmap>* out_images, WTF::Vector<::gfx::Size>* out_original_image_sizes)
{
    base::RunLoop loop;
    proxy_->DownloadImage(std::move(url), std::move(is_favicon), std::move(preferred_size), std::move(max_bitmap_size), std::move(bypass_cache),
        base::BindOnce(
            [](base::RunLoop* loop, int32_t* out_http_status_code, WTF::Vector<::SkBitmap>* out_images, WTF::Vector<::gfx::Size>* out_original_image_sizes,
                int32_t http_status_code, const WTF::Vector<::SkBitmap>& images, const WTF::Vector<::gfx::Size>& original_image_sizes) {
                *out_http_status_code = std::move(http_status_code);
                *out_images = std::move(images);
                *out_original_image_sizes = std::move(original_image_sizes);
                loop->Quit();
            },
            &loop, out_http_status_code, out_images, out_original_image_sizes));
    loop.Run();
}

