template <typename MaybeConstUserType> struct Serializer<::blink::mojom::PatternTemplateDataView, MaybeConstUserType> {
    using UserType = typename std::remove_const<MaybeConstUserType>::type;
    using Traits = UnionTraits<::blink::mojom::PatternTemplateDataView, UserType>;

    static void Serialize(MaybeConstUserType& input, MessageFragment<::blink::mojom::internal::PatternTemplate_Data>& fragment, bool inlined)
    {
        if (CallIsNullIfExists<Traits>(input)) {
            if (inlined)
                fragment->set_null();
            return;
        }

        if (!inlined)
            fragment.Allocate();

        // TODO(azani): Handle unknown and objects.
        // Set the not-null flag.
        fragment->size = kUnionDataSize;
        fragment->tag = Traits::GetTag(input);
        switch (fragment->tag) {
        case ::blink::mojom::PatternTemplateDataView::Tag::kFixed: {
            decltype(Traits::fixed(input)) in_fixed = Traits::fixed(input);
            mojo::internal::MessageFragment<typename decltype(fragment->data.f_fixed)::BaseType> value_fragment(fragment.message());
            mojo::internal::Serialize<::blink::mojom::FixedPatternDataView>(in_fixed, value_fragment);
            MOJO_INTERNAL_DLOG_SERIALIZATION_WARNING(
                value_fragment.is_null(), mojo::internal::VALIDATION_ERROR_UNEXPECTED_NULL_POINTER, "null fixed in PatternTemplate union");
            fragment->data.f_fixed.Set(value_fragment.is_null() ? nullptr : value_fragment.data());
            break;
        }
        case ::blink::mojom::PatternTemplateDataView::Tag::kFullWildcard: {
            decltype(Traits::full_wildcard(input)) in_full_wildcard = Traits::full_wildcard(input);
            mojo::internal::MessageFragment<typename decltype(fragment->data.f_full_wildcard)::BaseType> value_fragment(fragment.message());
            mojo::internal::Serialize<::blink::mojom::WildcardPatternDataView>(in_full_wildcard, value_fragment);
            MOJO_INTERNAL_DLOG_SERIALIZATION_WARNING(
                value_fragment.is_null(), mojo::internal::VALIDATION_ERROR_UNEXPECTED_NULL_POINTER, "null full_wildcard in PatternTemplate union");
            fragment->data.f_full_wildcard.Set(value_fragment.is_null() ? nullptr : value_fragment.data());
            break;
        }
        case ::blink::mojom::PatternTemplateDataView::Tag::kSegmentWildcard: {
            decltype(Traits::segment_wildcard(input)) in_segment_wildcard = Traits::segment_wildcard(input);
            mojo::internal::MessageFragment<typename decltype(fragment->data.f_segment_wildcard)::BaseType> value_fragment(fragment.message());
            mojo::internal::Serialize<::blink::mojom::WildcardPatternDataView>(in_segment_wildcard, value_fragment);
            MOJO_INTERNAL_DLOG_SERIALIZATION_WARNING(
                value_fragment.is_null(), mojo::internal::VALIDATION_ERROR_UNEXPECTED_NULL_POINTER, "null segment_wildcard in PatternTemplate union");
            fragment->data.f_segment_wildcard.Set(value_fragment.is_null() ? nullptr : value_fragment.data());
            break;
        }
        }
    }

    static bool Deserialize(::blink::mojom::internal::PatternTemplate_Data* input, UserType* output, Message* message)
    {
        if (!input || input->is_null())
            return CallSetToNullIfExists<Traits>(output);

        ::blink::mojom::PatternTemplateDataView data_view(input, message);
        return Traits::Read(data_view, output);
    }
};

