void V8Location::InstallInterfaceTemplate(v8::Isolate* isolate, const DOMWrapperWorld& world, v8::Local<v8::Template> interface_template)
{
    const WrapperTypeInfo* const wrapper_type_info = V8Location::GetWrapperTypeInfo();
    v8::Local<v8::FunctionTemplate> interface_function_template = interface_template.As<v8::FunctionTemplate>();
    v8::Local<v8::ObjectTemplate> instance_object_template = interface_function_template->InstanceTemplate();
    v8::Local<v8::ObjectTemplate> prototype_object_template = interface_function_template->PrototypeTemplate();
    v8::Local<v8::FunctionTemplate> parent_interface_template;
    bindings::SetupIDLInterfaceTemplate(
        isolate, wrapper_type_info, instance_object_template, prototype_object_template, interface_function_template, parent_interface_template);

    // Cross origin properties
    instance_object_template->SetAccessCheckCallbackAndHandler(CrossOriginAccessCheckCallback,
        v8::NamedPropertyHandlerConfiguration(CrossOriginNamedGetterCallback, CrossOriginNamedSetterCallback, CrossOriginNamedQueryCallback,
            CrossOriginNamedDeleterCallback, CrossOriginNamedEnumeratorCallback, CrossOriginNamedDefinerCallback, CrossOriginNamedDescriptorCallback,
            v8::Local<v8::Value>(), v8::PropertyHandlerFlags::kNone),
        v8::IndexedPropertyHandlerConfiguration(CrossOriginIndexedGetterCallback, CrossOriginIndexedSetterCallback,
            nullptr, // query
            CrossOriginIndexedDeleterCallback, CrossOriginIndexedEnumeratorCallback, CrossOriginIndexedDefinerCallback, CrossOriginIndexedDescriptorCallback,
            v8::Local<v8::Value>(), v8::PropertyHandlerFlags::kNone),
        v8::External::New(isolate, const_cast<WrapperTypeInfo*>(V8Location::GetWrapperTypeInfo())));

    v8::Local<v8::Template> instance_template = instance_object_template;
    // Location-specific settings
    // https://html.spec.whatwg.org/C/#the-location-interface
    // To create a Location object, run these steps:
    // step 3. Let valueOf be location's relevant
    //   Realm.[[Intrinsics]].[[%ObjProto_valueOf%]].
    // step 3. Perform ! location.[[DefineOwnProperty]]("valueOf",
    //   { [[Value]]: valueOf, [[Writable]]: false, [[Enumerable]]: false,
    //     [[Configurable]]: false }).
    instance_template->SetIntrinsicDataProperty(
        V8AtomicString(isolate, "valueOf"), v8::kObjProto_valueOf, static_cast<v8::PropertyAttribute>(v8::ReadOnly | v8::DontEnum | v8::DontDelete));
    // step 4. Perform ! location.[[DefineOwnProperty]](@@toPrimitive,
    //   { [[Value]]: undefined, [[Writable]]: false, [[Enumerable]]: false,
    //     [[Configurable]]: false }).
    instance_template->Set(
        v8::Symbol::GetToPrimitive(isolate), v8::Undefined(isolate), static_cast<v8::PropertyAttribute>(v8::ReadOnly | v8::DontEnum | v8::DontDelete));
    // 7.7.4.2 [[SetPrototypeOf]] ( V )
    // https://html.spec.whatwg.org/C/#location-setprototypeof
    instance_object_template->SetImmutableProto();
    prototype_object_template->SetImmutableProto();

    v8::Local<v8::Template> prototype_template = prototype_object_template;
    InstallUnconditionalProperties(isolate, world, instance_template, prototype_template, interface_template);
}

