{

    const ContextFeatureSettings* context_feature_settings
        = ContextFeatureSettings::From(execution_context, ContextFeatureSettings::CreationMode::kDontCreateIfNotExists);
    return (execution_context->IsWindow() || execution_context->IsWorkerGlobalScope()) && RuntimeEnabledFeatures::MojoJSEnabled()
        && (context_feature_settings && context_feature_settings->isMojoJSEnabled());
}

