bool V8IdentityProvider::IsExposed(ExecutionContext* execution_context)
{

    const bool is_in_secure_context = execution_context->IsSecureContext();
    return is_in_secure_context && execution_context->IsWindow() && RuntimeEnabledFeatures::FedCmEnabled();
}

