void V8PaintWorkletGlobalScope::InstallContextIndependentProperties(v8::Isolate* isolate, const DOMWrapperWorld& world,
    v8::Local<v8::Template> instance_template, v8::Local<v8::Template> prototype_template, v8::Local<v8::Template> interface_template)
{
    using bindings::IDLMemberInstaller;

    if (RuntimeEnabledFeatures::CSSColorTypedOMEnabled()) {
        static const IDLMemberInstaller::ExposedConstructConfig kExposedConstructTable[] = {
            { "CSSColorValue", CSSColorValueExposedConstructCallback },
            { "CSSHSL", CSSHSLExposedConstructCallback },
            { "CSSHWB", CSSHWBExposedConstructCallback },
            { "CSSRGB", CSSRGBExposedConstructCallback },
        };
        v8::Local<v8::FunctionTemplate> interface_function_template = interface_template.As<v8::FunctionTemplate>();
        v8::Local<v8::Signature> signature = v8::Signature::New(isolate, interface_function_template);
        IDLMemberInstaller::InstallExposedConstructs(
            isolate, world, instance_template, prototype_template, interface_template, signature, kExposedConstructTable);
    }
    if (RuntimeEnabledFeatures::Canvas2dCanvasFilterEnabled()) {
        static const IDLMemberInstaller::ExposedConstructConfig kExposedConstructTable[] = {
            { "CanvasFilter", CanvasFilterExposedConstructCallback },
        };
        v8::Local<v8::FunctionTemplate> interface_function_template = interface_template.As<v8::FunctionTemplate>();
        v8::Local<v8::Signature> signature = v8::Signature::New(isolate, interface_function_template);
        IDLMemberInstaller::InstallExposedConstructs(
            isolate, world, instance_template, prototype_template, interface_template, signature, kExposedConstructTable);
    }
}

void V8PaintWorkletGlobalScope::InstallContextDependentProperties(v8::Local<v8::Context> context, const DOMWrapperWorld& world,
    v8::Local<v8::Object> instance_object, v8::Local<v8::Object> prototype_object, v8::Local<v8::Object> interface_object,
    v8::Local<v8::Template> interface_template, FeatureSelector feature_selector)
{
    using bindings::IDLMemberInstaller;

    if (instance_object.IsEmpty()) {
        instance_object = context->Global()->GetPrototype().As<v8::Object>();
    }

    v8::Isolate* isolate = context->GetIsolate();
    ScriptState* script_state = ScriptState::From(isolate, context);
    ExecutionContext* execution_context = ToExecutionContext(script_state);
    if ((feature_selector.IsAll() && RuntimeEnabledFeatures::Canvas2dMeshEnabled(execution_context))
        || feature_selector.IsAnyOf(mojom::blink::OriginTrialFeature::kCanvas2dMesh)) {
        static const IDLMemberInstaller::ExposedConstructConfig kExposedConstructTable[] = {
            { "Mesh2DIndexBuffer", Mesh2DIndexBufferExposedConstructCallback },
            { "Mesh2DUVBuffer", Mesh2DUVBufferExposedConstructCallback },
            { "Mesh2DVertexBuffer", Mesh2DVertexBufferExposedConstructCallback },
        };
        v8::Local<v8::FunctionTemplate> interface_function_template = interface_template.As<v8::FunctionTemplate>();
        v8::Local<v8::Signature> signature = v8::Signature::New(isolate, interface_function_template);
        IDLMemberInstaller::InstallExposedConstructs(isolate, world, instance_object, prototype_object, interface_object, signature, kExposedConstructTable);
    }
}

