bool V8PressureObserver::IsExposed(ExecutionContext* execution_context)
{

    const bool is_in_secure_context = execution_context->IsSecureContext();
    return is_in_secure_context
        && (execution_context->IsDedicatedWorkerGlobalScope() || execution_context->IsSharedWorkerGlobalScope() || execution_context->IsWindow())
        && RuntimeEnabledFeatures::ComputePressureEnabled();
}

