void V8SpeechRecognition::InstallContextDependentProperties(v8::Local<v8::Context> context, const DOMWrapperWorld& world, v8::Local<v8::Object> instance_object,
    v8::Local<v8::Object> prototype_object, v8::Local<v8::Object> interface_object, v8::Local<v8::Template> interface_template,
    FeatureSelector feature_selector)
{
    using bindings::IDLMemberInstaller;

    if (feature_selector.IsAll()) {
        v8::Isolate* isolate = context->GetIsolate();
        // [LegacyNoInterfaceObject]
        // 3.7.3. Interface prototype object
        // https://webidl.spec.whatwg.org/#interface-prototype-object
        // step 13. If the [LegacyNoInterfaceObject] extended attribute was not
        //   specified on interface, then:
        //
        // V8 defines "constructor" property on the prototype object by default.
        prototype_object->Delete(context, V8AtomicString(isolate, "constructor")).ToChecked();
    }
}

